import { Component, ViewChild } from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatTableDataSource } from "@angular/material/table";
import { tap } from "rxjs/operators";
import { HackerNewsApiService as HackerNewsApiService } from "./hacker-news-api.service";
import { Title } from '@angular/platform-browser';

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})

export class AppComponent {
  newsStories: Array<any>;
  displayedColumns = ["storyTitle", "link"];
  dataSource: MatTableDataSource<any>;

  @ViewChild('paginator') paginator: MatPaginator;

  constructor(private hackerNewsApi: HackerNewsApiService, public title:Title) {}

  ngOnInit() {
    this.title.setTitle('NTHackerNewsFE');

    this.loadStories();
  }

  loadStories() {
    return this.hackerNewsApi
      .getLatestStories()
      .pipe(tap(data => {
        this.newsStories = <Array<any>>data;
      }));
  }

  ngAfterViewInit() {
    this.loadStories().subscribe(_ => {
      this.dataSource = new MatTableDataSource(this.newsStories);
      this.dataSource.paginator = this.paginator;

    });
  }

  search(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }
}
<div>
  <mat-form-field appearance="fill" class="mat-form-field-fill-start mat-form-field-fill-end" >
    <input matInput (keyup)="search($event)" placeholder="Search" #input />
  </mat-form-field>
</div>
<div>
    <mat-table [dataSource]="dataSource">
      <ng-container matColumnDef="storyTitle">
        <mat-header-cell *matHeaderCellDef>Story Title</mat-header-cell>
        <mat-cell class="mat-cell-story-title" *matCellDef="let story">{{story.title}}</mat-cell>
      </ng-container>

      <ng-container matColumnDef="link">
        <mat-header-cell *matHeaderCellDef>Link</mat-header-cell>
        <mat-cell class="mat-cell-story-title" *matCellDef="let story">
          <a href="{{ story.url }}" target="_blank" *ngIf="story.url; else Nolinkavailable">Go to Article</a>
        </mat-cell>
      </ng-container>
      <ng-template #Nolinkavailable>No link available</ng-template>
      <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>

      <mat-row *matRowDef="let row; columns: displayedColumns"></mat-row>
    </mat-table>
</div>
<div>
  <mat-paginator [pageSize]="10" #paginator></mat-paginator>
</div>
