import { Component, ViewChild } from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatTableDataSource } from "@angular/material/table";
import { tap } from "rxjs/operators";
import { NewsApiService as HackerNewsApiService } from "./hacker-news-api.service";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})

export class AppComponent {
  newsArticles: Array<any>;
  displayedColumns = ["articleTitle", "link"];
  dataSource: MatTableDataSource<any>;

  @ViewChild('paginator') paginator: MatPaginator;

  constructor(private hackerNewsApi: HackerNewsApiService) { }

  ngOnInit() {
    this.loadArticles();
  }

  loadArticles() {
    return this.hackerNewsApi
      .initArticles()
      .pipe(tap(data => {
        this.newsArticles = <Array<any>>data;
      }));
  }

  ngAfterViewInit() {
    this.loadArticles().subscribe(_ => {
      this.dataSource = new MatTableDataSource(this.newsArticles);
      this.dataSource.paginator = this.paginator;

    });
  }

  search(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

}
