import { Component, ViewChild } from "@angular/core";
import { MatPaginator } from "@angular/material/paginator";
import { MatTableDataSource } from "@angular/material/table";
import { map } from "rxjs/operators";
import { NewsApiService } from "./news-api.service";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})

export class AppComponent { //implements OnInit, AfterViewInit
  mArticles: Array<any>;
  displayedColumns = ["articleTitle", "link"];
  dataSource: MatTableDataSource<any>;

  @ViewChild('paginator') paginator: MatPaginator;

  constructor(private newsapi: NewsApiService) {

  }

  ngOnInit() {
    this.loadArticles();
    console.log(this.mArticles);
  }

  loadArticles() {
    return this.newsapi
      .initArticles()
      .pipe(map(data => {
        console.log(data);
        //this.mArticles = data["articles"];
      }));
  }

  ngAfterViewInit() {
    this.loadArticles().subscribe(_ => {
      console.log(this.mArticles);
      this.dataSource = new MatTableDataSource(this.mArticles);
      this.dataSource.paginator = this.paginator;

    });
  }

  // searchArticles(source) {
  //   console.log("selected source is: " + source);
  //   this.newsapi
  //     .getArticlesByID(source)
  //     .subscribe((data) => (this.mArticles = data["articles"]));
  // }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    console.log(filterValue);
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

}
<div>
  <mat-form-field class="search-box-full-width-height">
    <input matInput (keyup)="applyFilter($event)" placeholder="Search" #input />
  </mat-form-field>
</div>
<div>
  <mat-sidenav-container class="mat-drawer-content-custom">
    <mat-table [dataSource]="dataSource">
      <ng-container matColumnDef="articleTitle">
        <mat-header-cell *matHeaderCellDef>Article Title</mat-header-cell>
        <mat-cell class="mat-cell-articletitle" *matCellDef="let article">{{article.title}}</mat-cell>
      </ng-container>

      <ng-container matColumnDef="link">
        <mat-header-cell *matHeaderCellDef>Link</mat-header-cell>
        <mat-cell class="mat-cell-articletitle" *matCellDef="let article">
          <a href="{{ article.url }}" target="_blank" *ngIf="article.url">Go to Article</a>
        </mat-cell>
      </ng-container>

      <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>

      <mat-row *matRowDef="let row; columns: displayedColumns"></mat-row>
    </mat-table>
  </mat-sidenav-container>
</div>
<div>
  <mat-paginator [pageSize]="4" #paginator></mat-paginator>
</div>
